import React, { useState } from 'react';

function App() {
  const [scanDate, setScanDate] = useState('');
  const [harvestDate, setHarvestDate] = useState('');
  const [growthRate, setGrowthRate] = useState('');
  const [minDiameter, setMinDiameter] = useState(20);
  const [maxDiameter, setMaxDiameter] = useState(120);
  const [histogram, setHistogram] = useState([]);
  const [avgSize, setAvgSize] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!scanDate || !harvestDate || !growthRate) {
      alert('Please fill all inputs.');
      return;
    }
    setLoading(true);
    try {
      const response = await fetch('http://34.44.41.132:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          scan_date: scanDate,
          harvest_date: harvestDate,
          growth_rate: parseFloat(growthRate),
          min_diameter: parseFloat(minDiameter),
          max_diameter: parseFloat(maxDiameter),
        }),
      });
      const data = await response.json();
      setAvgSize(data.average_size);
      setHistogram(data.histogram);
    } catch (error) {
      alert('Failed to connect to backend: ' + error.message);
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Fruit Harvest Size Predictor</h1>
      <div>
        <label>Scan Date: </label>
        <input type="date" value={scanDate} onChange={e => setScanDate(e.target.value)} />
      </div>
      <div>
        <label>Harvest Date: </label>
        <input type="date" value={harvestDate} onChange={e => setHarvestDate(e.target.value)} />
      </div>
      <div>
        <label>Growth Rate (mm³/day): </label>
        <input type="number" value={growthRate} onChange={e => setGrowthRate(e.target.value)} />
      </div>
      <div>
        <label>Min Diameter (mm): </label>
        <input
          type="range"
          min="20"
          max="120"
          value={minDiameter}
          onChange={e => setMinDiameter(e.target.value)}
        />
        <span>{minDiameter}</span>
      </div>
      <div>
        <label>Max Diameter (mm): </label>
        <input
          type="range"
          min="20"
          max="120"
          value={maxDiameter}
          onChange={e => setMaxDiameter(e.target.value)}
        />
        <span>{maxDiameter}</span>
      </div>
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? 'Calculating...' : 'Submit'}
      </button>

      {avgSize && (
        <div>
          <h2>Average Predicted Volume: {avgSize.toFixed(2)} mm³</h2>
          <h3>Histogram (Volume bins)</h3>
          <ul>
            {histogram.map(({ bin_start, count }) => (
              <li key={bin_start}>
                {bin_start} - {bin_start + 5000}: {count}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
