import React, { useState } from 'react';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, Legend,
} from 'recharts';

const App = () => {
  const [scanDate, setScanDate] = useState('');
  const [harvestDate, setHarvestDate] = useState('');
  const [growthRate, setGrowthRate] = useState('');
  const [minDiameter, setMinDiameter] = useState(20);
  const [maxDiameter, setMaxDiameter] = useState(120);
  const [histogram, setHistogram] = useState([]);
  const [avgSize, setAvgSize] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    setError('');
    if (!scanDate || !harvestDate || !growthRate) {
      setError('Please fill in all required fields.');
      return;
    }
    if (new Date(scanDate) >= new Date(harvestDate)) {
      setError('Harvest date must be after scan date.');
      return;
    }
    if (minDiameter > maxDiameter) {
      setError('Min diameter cannot be greater than max diameter.');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('http://34.44.41.132:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          scan_date: scanDate,
          harvest_date: harvestDate,
          growth_rate: parseFloat(growthRate),
          min_diameter: parseFloat(minDiameter),
          max_diameter: parseFloat(maxDiameter),
        }),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.statusText}`);
      }

      const data = await response.json();
      setAvgSize(data.average_size);
      setHistogram(data.histogram);
    } catch (err) {
      setError(`Failed to connect to backend: ${err.message}`);
      setAvgSize(null);
      setHistogram([]);
    }
    setLoading(false);
  };

  // Format data for Recharts
  const chartData = histogram.map(bin => ({
    volumeRange: `${bin.bin_start} - ${bin.bin_start + 5000}`,
    count: bin.count,
  }));

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üçé Fruit Harvest Size Predictor üçé</h1>

      <div style={styles.form}>
        <label style={styles.label}>
          Scan Date*:
          <input
            type="date"
            value={scanDate}
            onChange={e => setScanDate(e.target.value)}
            style={styles.input}
          />
        </label>

        <label style={styles.label}>
          Harvest Date*:
          <input
            type="date"
            value={harvestDate}
            onChange={e => setHarvestDate(e.target.value)}
            style={styles.input}
          />
        </label>

        <label style={styles.label}>
          Growth Rate (mm¬≥/day)*:
          <input
            type="number"
            placeholder="e.g. 1000"
            value={growthRate}
            onChange={e => setGrowthRate(e.target.value)}
            style={styles.input}
            min="0"
          />
        </label>

        <label style={styles.label}>
          Min Diameter (mm):
          <input
            type="range"
            min="20"
            max="120"
            value={minDiameter}
            onChange={e => setMinDiameter(Number(e.target.value))}
            style={styles.range}
          />
          <span style={styles.rangeValue}>{minDiameter}</span>
        </label>

        <label style={styles.label}>
          Max Diameter (mm):
          <input
            type="range"
            min="20"
            max="120"
            value={maxDiameter}
            onChange={e => setMaxDiameter(Number(e.target.value))}
            style={styles.range}
          />
          <span style={styles.rangeValue}>{maxDiameter}</span>
        </label>

        <button
          onClick={handleSubmit}
          disabled={loading}
          style={loading ? styles.buttonDisabled : styles.button}
        >
          {loading ? 'Calculating...' : 'Submit'}
        </button>

        {error && <p style={styles.error}>{error}</p>}
      </div>

      {avgSize !== null && (
        <div style={styles.results}>
          <h2>Average Predicted Volume: {avgSize.toFixed(2)} mm¬≥</h2>

          {histogram.length === 0 ? (
            <p>No fruits found in the selected diameter range.</p>
          ) : (
            <ResponsiveContainer width="100%" height={350}>
              <BarChart
                data={chartData}
                margin={{ top: 20, right: 40, left: 20, bottom: 60 }}
                barSize={30}
              >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis
                  dataKey="volumeRange"
                  angle={-45}
                  textAnchor="end"
                  interval={0}
                  height={60}
                  tick={{ fontSize: 12 }}
                />
                <YAxis allowDecimals={false} />
                <Tooltip />
                <Legend />
                <Bar dataKey="count" fill="#4caf50" />
              </BarChart>
            </ResponsiveContainer>
          )}
        </div>
      )}

      <footer style={styles.footer}>
        <small>Powered by Flask & React | Your Name</small>
      </footer>
    </div>
  );
};

// Inline styles (for simplicity)
const styles = {
  container: {
    maxWidth: 720,
    margin: 'auto',
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#333',
  },
  title: {
    textAlign: 'center',
    marginBottom: 30,
    color: '#2e7d32',
  },
  form: {
    display: 'grid',
    gridTemplateColumns: '1fr',
    gap: 15,
    marginBottom: 40,
  },
  label: {
    display: 'flex',
    flexDirection: 'column',
    fontWeight: '600',
    fontSize: 14,
  },
  input: {
    marginTop: 5,
    padding: '8px 10px',
    fontSize: 14,
    borderRadius: 4,
    border: '1px solid #ccc',
  },
  range: {
    width: '100%',
    marginTop: 8,
  },
  rangeValue: {
    marginLeft: 10,
    fontWeight: '600',
  },
  button: {
    backgroundColor: '#4caf50',
    border: 'none',
    padding: '12px 0',
    color: 'white',
    fontWeight: 'bold',
    fontSize: 16,
    cursor: 'pointer',
    borderRadius: 5,
    marginTop: 10,
  },
  buttonDisabled: {
    backgroundColor: '#a5d6a7',
    border: 'none',
    padding: '12px 0',
    color: 'white',
    fontWeight: 'bold',
    fontSize: 16,
    borderRadius: 5,
    marginTop: 10,
    cursor: 'not-allowed',
  },
  error: {
    marginTop: 10,
    color: '#d32f2f',
    fontWeight: '600',
  },
  results: {
    textAlign: 'center',
  },
  footer: {
    marginTop: 50,
    textAlign: 'center',
    color: '#999',
  },
};

export default App;
